import React, { Component } from "react";
import DatePicker from "react-datepicker";
import { Container, Label, Input, FormGroup, Form, Button } from "reactstrap";
import { Link } from "react-router-dom";
import "react-datepicker/dist/react-datepicker.css";
import "./Expenses.css";

class Expenses extends Component {
  state = {
    isLoading: true,
    expenses: [],
    categories,
    date: new Date()
  };

  async componentDidMount() {
    const responseExp = await fetch("/api/expenses");
    const bodyExp = await responseExp.json();
    this.setState({ expenses: bodyExp });

    const responseCat = await fetch("/api/categories");
    const bodyCat = await responseCat.json();
    this.setState({ expenses: bodyCat, isLoading: false });
  }

  render() {
    const title = <h3>Add an Expense</h3>;
    const { expenses, isLoading } = this.state;
    if (isLoading) {
      return <div>Loading...</div>;
    }
    return (
      <div>
        <h2>Expenses</h2>

        <Container>
          {title}
          <Form onSubmit={this.handleSubmit}>
            <FormGroup>
              <Label for="title">Expense Name</Label>
              <Input
                type="text"
                name="title"
                id="title"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="category">Category</Label>
              <Input
                type="text"
                name="category"
                id="category"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="expenseDate">Expense Date</Label>{" "}
              <DatePicker
                selected={this.state.date}
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="location">Location</Label>
              <Input
                type="text"
                name="location"
                id="location"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Button type="submit" color="primary">
                Save
              </Button>{" "}
              <Button color="warning" tag={Link} to="/">
                Cancel
              </Button>
            </FormGroup>
          </Form>
        </Container>

        {/* 
        {expenses.map(expense => (
          <div id={expense.id}>{expense.name}</div>
        ))}
        */}
      </div>
    );
  }
}

export default Expenses;
