import axios from "axios";
import {
  ACCESS_TOKEN,
  SECRET,
  ADMIN_ID,
  ADMIN_STRING
} from "../Components/Constants/index.js";
var jwt = require("jsonwebtoken");

class Authenticate {
  // constructor

  // methods
  isLoggedIn() {
    let token = localStorage.getItem(ACCESS_TOKEN);
    if (token) {
      // add -> redux, if decodedtoken sub is XXX then make admin, else normal user
      return true;
    }
    return false;
  }

  /* Function will access the local storage for the access token and return the sub # which is relevant to the user ID */
  getUserId() {
    let token = localStorage.getItem(ACCESS_TOKEN);
    let decode = jwt.verify(token, SECRET);
    return decode.sub;
  }

  isAdmin(userId) {
    let flag = false;
    if (userId === ADMIN_ID) {
      axios.get(`/api/users/${userId}`).then(res => {
        const rolesArray = res.data.roles;
        rolesArray.forEach(element => {
          if (element.name === ADMIN_STRING) {
            console.log(element.name, ADMIN_STRING);
            flag = true;
          }
        });
        return flag;
      });
    }
  }

  removeToken() {
    localStorage.removeItem(ACCESS_TOKEN);
  }
}
export default new Authenticate();
