import React, { Component } from "react";
import DatePicker from "react-datepicker";
import { Container, Label, Input, FormGroup, Form, Button } from "reactstrap";
import { Link } from "react-router-dom";
import "react-datepicker/dist/react-datepicker.css";
import "./Expenses.css";

class Expenses extends Component {
  state = {
    isLoading: true,
    expenses: [],
    categories: [],
    date: new Date()
  };

  async componentDidMount() {
    const responseExp = await fetch("/api/expenses");
    const bodyExp = await responseExp.json();
    this.setState({ expenses: bodyExp });

    const responseCat = await fetch("/api/categories");
    const bodyCat = await responseCat.json();
    this.setState({ categories: bodyCat, isLoading: false });
  }

  render() {
    const addExpenseTitle = <h3>Add an Expense</h3>;
    const currentExpenseTitle = <h3>Existing Expenses</h3>;
    const { expenses, categories, isLoading } = this.state;

    let categoryList = categories.map(element => (
      <option id={element.name}>{element.name}</option>
    ));

    let expenseList = expenses.map(element => (
      <tr key={element.id}>
        <td>{element.id}</td>
        <td>{element.name}</td>
        <td>{element.price}</td>
        <td>{element.description}</td>
        <td>{element.location}</td>
        <td>{element.category.name}</td>
        <td>{element.user.name}</td>
      </tr>
    ));

    if (isLoading) {
      return <div>Loading...</div>;
    }

    return (
      <div>
        <h2>Expenses</h2>

        <Container>
          {addExpenseTitle}
          <Form onSubmit={this.handleSubmit}>
            <FormGroup>
              <Label for="title">Expense Name</Label>
              <Input
                type="text"
                name="title"
                id="title"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="price">Price (In USD)</Label>
              <Input
                type="number"
                step="0.1"
                name="price"
                id="price"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="category">Category</Label>{" "}
              <select onChange={this.handleChange}>{categoryList}</select>
            </FormGroup>

            <FormGroup>
              <Label for="expenseDate">Expense Date</Label>{" "}
              <DatePicker
                selected={this.state.date}
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="location">Location</Label>
              <Input
                type="text"
                name="location"
                id="location"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Button type="submit" color="primary">
                Save
              </Button>{" "}
              <Button color="warning" tag={Link} to="/">
                Cancel
              </Button>
            </FormGroup>
          </Form>
        </Container>

        <Container>
          {currentExpenseTitle}
          <Table className="mt-4">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Price (USD)</th>
                <th>Description</th>
                <th>Location</th>
                <th>Category</th>
                <th>User</th>

                <td>{element.id}</td>
                <td>{element.name}</td>
                <td>{element.price}</td>
                <td>{element.description}</td>
                <td>{element.location}</td>
                <td>{element.category.name}</td>
                <td>{element.user.name}</td>
              </tr>
            </thead>
            <tbody>{expenseList}</tbody>
          </Table>
        </Container>
      </div>
    );
  }
}

export default Expenses;
