import React, { Component } from "react";
import axios from "axios";
import {
  Container,
  Label,
  Input,
  FormGroup,
  Form,
  Button,
  Alert
} from "reactstrap";
import { Link } from "react-router-dom";

class Register extends Component {
  // constructor
  constructor() {
    super();
    this.state = {
      userPayload: {
        name: String,
        email: String,
        password: String
      },
      errorMessage: false
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  // methods
  async handleSubmit(event) {
    event.preventDefault();
    const signupPayload = this.state.userPayload;

    const config = {
      headers: {
        "content-type": "application/json"
      }
    };

    await axios
      .post(`/api/auth/signup`, JSON.stringify(signupPayload), config)
      .then(res => {
        if (res.status === 201) {
          console.log(res);
          console.log(signupPayload);
          this.setState({ userAdded: true });
          console.log("USER ADDED");
          this.props.history.push("/login");
        } else {
          console.log("Eror posting to user");
          console.log(res.status);
        }

        //event.target.reset();
      })
      .catch(err => {
        console.log(err.response.status);
      });
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    let userPayload = { ...this.state.userPayload };
    userPayload[name] = value;
    this.setState({ userPayload });
  }

  render() {
    const addUserTitle = <h3>Add User</h3>;
    return (
      <div>
        {/*Add users */}
        <Container>
          {addUserTitle}
          {this.state.errorMessage ? (
            <Alert color="danger">Error registering user</Alert>
          ) : null}

          <Form onSubmit={this.handleSubmit}>
            <FormGroup>
              <Label for="name">Name</Label>
              <Input
                type="text"
                name="name"
                id="name"
                placeholder="John Wick"
                required
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="email">Email</Label>
              <Input
                type="email"
                name="email"
                id="email"
                required
                placeholder="DogLuver@gmail.com"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="password">Password</Label>
              <Input
                type="password"
                name="password"
                id="password"
                required
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Button type="submit" color="primary">
                Save
              </Button>{" "}
              <Button color="warning" tag={Link} to="/">
                Cancel
              </Button>
            </FormGroup>
          </Form>
        </Container>
      </div>
    );
  }
}

export default Register;
