import React, { Component } from "react";
import axios from "axios";
import {
  Container,
  Label,
  Input,
  FormGroup,
  Form,
  Button,
  Alert
} from "reactstrap";

import { Link } from "react-router-dom";
import { ACCESS_TOKEN } from "../Constants/index";
import NavigationBar from "../NavigationBar";
import { connect } from "react-redux";
import { login } from "../../Redux/Actions/authorizeActions";

class Login extends Component {
  //constructor
  constructor() {
    super();
    this.state = {
      loginPayload: {
        email: String,
        password: String
      },
      loginErorrStatus: false,
      redirected: false
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    // props is from the protectedRoute component
    if (this.props.location.state) {
      this.setState({ redirected: true });
    }
  }

  // methods
  /*
  async handleSubmit(event) {
    event.preventDefault();
    const signinPayload = this.state.loginPayload;

    const config = {
      headers: {
        "content-type": "application/json"
      }
    };

    await axios
      .post(`/api/auth/signin`, JSON.stringify(signinPayload), config)
      .then(res => {
        if (res.status === 200) {
          ///////////////////////////////////////
          if (localStorage.getItem(ACCESS_TOKEN)) {
            localStorage.removeItem(ACCESS_TOKEN);
          }
          let accessToken = res.data.accessToken;
          localStorage.setItem(ACCESS_TOKEN, accessToken);
          /////////////////////////////////////////////////////
          // If the user was redirected via protected route, there will be a "from" prop obj
          let redirect = this.state.redirected
            ? this.props.location.state.from
            : "/";
          this.props.history.push(redirect);
        }
      })
      .catch(err => {
        this.setState({ loginErorrStatus: true });
        ///////////////////////////////////////////////////
        console.log("ERROR POSTING TO LOGIN");
        console.log(err);
        /////////////////////////////////////////////////
      });
  }
  */
  ///////////////////////////////////////////////////////////////////////////

  async handleSubmit(event) {
    event.preventDefault();
    const signinPayload = this.state.loginPayload;

    const config = {
      headers: {
        "content-type": "application/json"
      }
    };

    console.log("before await");
    await this.props.login(signinPayload, config);
    console.log("after");

    // If the user was redirected via protected route, there will be a "from" prop obj
    let redirect = this.state.redirected ? this.props.location.state.from : "/";
    console.log(redirect);
    console.log("pushing");
    console.log(this.props.history);
    this.props.history.push(redirect);
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    let loginPayload = { ...this.state.loginPayload };
    loginPayload[name] = value;
    this.setState({ loginPayload });
  }

  render() {
    const loginTitle = <h2>Login</h2>;
    return (
      <div>
        <NavigationBar />
        <Container>
          {loginTitle}

          {this.props.showLoginError ? (
            <Alert color="danger">Error logging in!</Alert>
          ) : null}
          {this.state.redirected ? (
            <Alert color="danger">
              You must be logged in to view that page!
            </Alert>
          ) : null}
          <Form onSubmit={this.handleSubmit}>
            <FormGroup>
              <Label for="email">Email</Label>
              <Input
                type="email"
                name="email"
                id="email"
                required
                placeholder="DogLuver@gmail.com"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="password">Password</Label>
              <Input
                type="password"
                name="password"
                id="password"
                required
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Button type="submit" color="primary">
                Login
              </Button>{" "}
              <Button color="link" tag={Link} to="/register">
                Register a new account
              </Button>
            </FormGroup>
          </Form>
        </Container>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    showLoginError: state.auth.loginError
  };
};

export default connect(
  mapStateToProps,
  { login }
)(Login);

/*
  the above export is object shorthand form for mapping actions as props
  : https://react-redux.js.org/api/connect
*/
