import React, { Component } from "react";
import axios from "axios";
import {
  Container,
  Label,
  Input,
  FormGroup,
  Form,
  Button,
  Table
} from "reactstrap";
import { Link } from "react-router-dom";

class Users extends Component {
  // constructor
  constructor() {
    super();
    this.state = {
      isLoading: true,
      users: [],
      userPayload: {
        name: String,
        email: String,
        password: String
      }
    };
    this.delete = this.delete.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  // life cycle
  async componentDidMount() {
    await axios.get("/api/users").then(res => {
      const users = res.data;
      this.setState({ users, isLoading: false });
    });
  }

  // methods
  delete(id) {
    axios.delete(`/api/user/${id}`).then(res => {
      let updatedUsers = [...this.state.users].filter(
        element => element.id !== id
      );
      this.setState({ users: updatedUsers });
    });
  }

  handleSubmit(event) {}

  handleSubmit(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    let userPayload = { ...this.state.userPayload };
    userPayload[name] = value;
    this.setState({ userPayload });
    console.log(userPayload);
  }

  // render
  render() {
    const addUserTitle = <h3>Add User</h3>;
    const currentUserTitle = <h3>Existing Users</h3>;
    const { users } = this.state;

    let userList = users.map(element => (
      <tr key={element.id}>
        <td>{element.id}</td>
        <td>{element.name}</td>
        <td>{element.email}</td>
        <td>
          <Button
            size="sm"
            color="danger"
            onClick={() => this.delete(element.id)}
          >
            X
          </Button>
        </td>
      </tr>
    ));
    return (
      <div>
        <h2>Users</h2>
        {/*Add users */}
        <Container>
          {addUserTitle}
          <Form onSubmit={this.handleSubmit}>
            <FormGroup>
              <Label for="userName">Name</Label>
              <Input
                type="text"
                name="userName"
                id="userName"
                placeholder="John Wick"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="email">Email</Label>
              <Input
                type="email"
                name="email"
                id="email"
                placeholder="DogLuver@gmail.com"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="password">Password</Label>
              <Input
                type="password"
                name="password"
                id="password"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Button type="submit" color="primary">
                Save
              </Button>{" "}
              <Button color="warning" tag={Link} to="/">
                Cancel
              </Button>
            </FormGroup>
          </Form>
        </Container>
        <br />
        <hr />
        <br />
        {/*Current users */}
        <Container>
          {currentUserTitle}
          <Table className="mt-4">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Email</th>
                <th></th>
              </tr>
            </thead>
            <tbody>{userList}</tbody>
          </Table>
        </Container>
      </div>
    );
  }
}

export default Users;
