var jwt = require("jsonwebtoken");
import { ACCESS_TOKEN, SECRET } from "../Constants/index";

class Authenticate {
  // constructor
  constructor() {}

  // methods
  isLoggedIn() {
    let token = localStorage.getItem(ACCESS_TOKEN);
    if (token) {
      // add -> redux, if decodedtoken sub is XXX then make admin, else normal user
      return true;
    }
    return false;
  }

  /* Function will access the local storage for the access token and return the sub # which is relevant to the user ID */
  getUser() {
    let token = localStorage.getItem(ACCESS_TOKEN);
    let decode = jwt.verify(token, SECRET);
    return decode.sub;
  }
}
export default new Authenticate();
