import axios from "axios";
import { LOGIN, LOGOUT } from "../../Components/Constants/index";
import authenticate from "../../Utils/authenicate";
import { ACCESS_TOKEN } from "../../Components/Constants/index";

export const login = (payload, config) => dispatch => {
  axios
    .post(`/api/auth/signin`, payload, config)
    .then(res => {
      if (localStorage.getItem(ACCESS_TOKEN)) {
        localStorage.removeItem(ACCESS_TOKEN);
      }
      let accessToken = res.data.accessToken;
      localStorage.setItem(ACCESS_TOKEN, accessToken);
    })
    .then(res => {
      let userId = authenticate.getUserId();
      let isAdminBool = authenticate.isAdmin(userId);
      dispatch({
        type: LOGIN,
        payload: {
          isLoggedIn: true,
          userId: userId, //number
          isAdmin: isAdminBool //
        }
      });
    })
    .catch(err => {
      console.log("ERROR POSTING TO LOGIN");
      console.log(err);
    });
};

export const logout = () => dispatch => {};
