import React, { Component } from "react";
import axios from "axios";
import {
  Container,
  Label,
  Input,
  FormGroup,
  Form,
  Button,
  Table
} from "reactstrap";
import { Link } from "react-router-dom";

class Users extends Component {
  // constructor
  constructor() {
    super();
    this.state = {
      isLoading: true,
      users: []
    };
  }

  // life cycle
  async componentDidMount() {
    await axios.get("/api/users").then(res => {
      const users = res.data;
      this.setState({ users, isLoading: false });
    });
  }

  // methods

  delete(id) {
    axios.delete(`/api/user/${id}`).then(res => {
      let updatedUsers = [...this.state.users].filter(
        element => element.id !== id
      );
      this.setState({ users: updatedUsers });
    });
  }

  // render
  render() {
    const addUserTitle = <h3>Add User</h3>;
    const currentUserTitle = <h3>Existing Users</h3>;
    const { users } = this.state;

    let userList = users.map(element => (
      <tr key={element.id}>
        <td>{element.id}</td>
        <td>{element.name}</td>
        <td>{element.email}</td>
        <td>
          <Button
            size="sm"
            color="danger"
            onClick={() => this.delete(element.id)}
          >
            X
          </Button>
        </td>
      </tr>
    ));
    return (
      <div>
        <h2>Users</h2>
        {/*Add users */}
        <Container>{addUserTitle}</Container>
        {/*Current users */}
        <Container>
          {currentUserTitle}
          <Table className="mt-4">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Email</th>
                <th></th>
              </tr>
            </thead>
            <tbody>{userList}</tbody>
          </Table>
        </Container>
      </div>
    );
  }
}

export default Users;
