import { logInUser, logOutUser } from "../actions/authorizeActions.js";
import { ACCESS_TOKEN } from "../../Constants/index.js";

let token = localStorage.getItem(ACCESS_TOKEN);
const initalState = token ? { loggedIn: true } : { loggedIn: false };
export function authorizeReducer(state = initalState, { type, payload }) {
  switch (type) {
    case "LOGIN":
      return { loggedIn: true };
    case "LOGOUT":
      return { loggedIn: false };
    default:
      return { state };
  }
}
