import React, { Component } from "react";
import {
  Container,
  Label,
  Input,
  FormGroup,
  Form,
  Button,
  Table
} from "reactstrap";

class Category extends Component {
  // constructor
  constructor() {
    super();
    this.state = {
      isLoading: true,
      categories: [],
      errorMessage: false,
      categoryPayload: {
        category: String
      }
    };
  }

  // life cycle
  async componentDidMount() {
    const response = await fetch("/api/categories"); // proxy in package.json is set to the server the back end is on
    const body = await response.json();
    this.setState({ categories: body, isLoading: false });
  }

  // methods
  async handleSubmit(event) {
    event.preventDefault();
    const signupPayload = this.state.userPayload;

    const config = {
      headers: {
        "content-type": "application/json"
      }
    };

    await axios
      .post(`/api/auth/signup`, JSON.stringify(signupPayload), config)
      .then(res => {
        if (res.status === 201) {
          console.log(res);
          console.log(signupPayload);
          this.setState({ userAdded: true });
          console.log("USER ADDED");
          this.props.history.push("/login");
        } else {
          console.log("Eror posting to user");
          console.log(res.status);
        }

        //event.target.reset();
      })
      .catch(err => {
        if (err.response.status === 409) {
          console.log("ERROR 409 rcvd. email already exists.");
          this.setState({ errorMessage: true });
        }
      });
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    let categoryPayload = { ...this.state.categoryPayload };
    categoryPayload[name] = value;
    this.setState({ categoryPayload });
  }

  //render
  render() {
    const { categories, isLoading } = this.state;
    const addCategoryTitle = <h3>Add a new Category</h3>;
    const existingCategoryTitle = <h3>Existing Category</h3>;

    let categoryList = categories.map(element => (
      <tr key={element.id}>
        <td>{element.id}</td>
        <td>{element.name}</td>
        <td>
          <Button
            size="sm"
            color="danger"
            onClick={() => this.delete(element.id)}
          >
            X
          </Button>
        </td>
      </tr>
    ));

    if (isLoading) {
      return <div>Loading...</div>;
    }
    return (
      <div>
        <Container>
          {addCategoryTitle}
          <Form onSubmit={this.handleSubmit}>
            <FormGroup>
              <Label for="title">Category Name</Label>
              <Input
                type="text"
                name="name"
                id="name"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Button type="submit" color="primary">
                Save
              </Button>{" "}
              <Button color="warning" tag={Link} to="/">
                Cancel
              </Button>
            </FormGroup>
          </Form>

          <br />
          <hr />
          <br />
          {existingCategoryTitle}
          <Table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th></th>
              </tr>
            </thead>
            <tbody>{categoryList}</tbody>
          </Table>
        </Container>
      </div>
    );
  }
}

export default Category;
