import React, { Component } from "react";
import axios from "axios";
import {
  Container,
  Label,
  Input,
  FormGroup,
  Form,
  Button,
  Table,
  Alert
} from "reactstrap";
import { Link } from "react-router-dom";

class Users extends Component {
  // constructor
  constructor() {
    super();
    this.state = {
      isLoading: true,
      users: [],
      userPayload: {
        name: String,
        email: String,
        password: String
      },
      userAdded: false
    };
    this.delete = this.delete.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  // life cycle
  async componentDidMount() {
    await axios.get("/api/users").then(res => {
      const users = res.data;
      this.setState({ users, isLoading: false });
    });
  }

  // methods
  delete(id) {
    axios.delete(`/api/user/${id}`).then(res => {
      let updatedUsers = [...this.state.users].filter(
        element => element.id !== id
      );
      this.setState({ users: updatedUsers });
    });
  }

  async handleSubmit(event) {
    const signupPayload = this.state.userPayload;

    const config = {
      headers: {
        "content-type": "application/json"
      }
    };

    console.log("Before axios call");
    await axios
      .post(`/api/auth/signup`, JSON.stringify(signupPayload), config)
      .then(res => {
        res.JSON().then(json => {
          if (res.status !== 201) {
            alert("Error posting new user.");
          }
          if (res.status === 201) {
            this.setState({ userList: true });
            alert("User added");
            console.log("USER ADDED");
          }
        });

        //event.target.reset();
      })
      .catch(err => {
        console.log("ERROR POSTING TO USER");
        console.log(err);
      });
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    let userPayload = { ...this.state.userPayload };
    userPayload[name] = value;
    this.setState({ userPayload });
  }

  // render
  render() {
    const addUserTitle = <h3>Add User</h3>;
    const currentUserTitle = <h3>Existing Users</h3>;
    const { users } = this.state;

    let userList = users.map(element => (
      <tr key={element.id}>
        <td>{element.id}</td>
        <td>{element.name}</td>
        <td>{element.email}</td>
        <td>
          <Button
            size="sm"
            color="danger"
            onClick={() => this.delete(element.id)}
          >
            X
          </Button>
        </td>
      </tr>
    ));
    return (
      <div>
        <h2>Users</h2>
        {/*Add users */}
        <Container>
          {addUserTitle}
          {this.state.userAdded ? (
            <Alert color="success">User added successfully!</Alert>
          ) : null}

          <Form onSubmit={this.handleSubmit}>
            <FormGroup>
              <Label for="name">Name</Label>
              <Input
                type="text"
                name="name"
                id="name"
                placeholder="John Wick"
                required
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="email">Email</Label>
              <Input
                type="email"
                name="email"
                id="email"
                required
                placeholder="DogLuver@gmail.com"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label for="password">Password</Label>
              <Input
                type="password"
                name="password"
                id="password"
                required
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Button type="submit" color="primary">
                Save
              </Button>{" "}
              <Button color="warning" tag={Link} to="/">
                Cancel
              </Button>
            </FormGroup>
          </Form>
        </Container>
        <br />
        <hr />
        <br />
        {/*Current users */}
        <Container>
          {currentUserTitle}
          <Table className="mt-4">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Email</th>
                <th></th>
              </tr>
            </thead>
            <tbody>{userList}</tbody>
          </Table>
        </Container>
      </div>
    );
  }
}

export default Users;
